public static IEnumerable<T> Reverse<T>(this IEnumerable<T> items)
        {
            IList<T> list = (IList<T>) items;
            
            if (list == null) 
                yield return default(T);

            for (int i = list.Count - 1; i >= 0; i--)
            {
                yield return list[i];
            }
        }

