public static void TryDispose(this object target)
{
  TryDispose(target, false);
}

public static void TryDispose(this object target, bool throwException)
{
  IDisposable disposable = target as IDisposable;
  if (disposable == null)
      return;

  try
  {
    disposable.Dispose();
  }
  catch (Exception)
  {
    if (throwException)
      throw;
  }
}
