public static double inet_aton(this IPAddress IPaddress)
{
    int i;
    double num = 0;
    if (IPaddress.ToString() == "")
    {
	return 0;
    }
    string[] arrDec = IPaddress.ToString().Split('.');
    for (i = arrDec.Length - 1; i >= 0; i--)
    {
	num += ((int.Parse(arrDec[i]) % 256) * Math.Pow(256, (3 - i)));
    }
    return num;
}
