public static void ClearControls(this System.Windows.Forms.Form form)
{
   foreach(var item in form.Controls)
   {
      if (item is TextBox)
         (item as TextBox).Text = "";
      
      if (item is ComboBox)
         (item as ComboBox).SelectedIndex = -1;

      //if (item is ...)
   }

}
