public static T To<T>(this IConvertible value)
        {
            try
            {
                return (T) Convert.ChangeType(value, typeof (T));
            }
            catch
            {
                return default(T);
            }
        }

var age = "28";
var intAge = age.To<int>();
var doubleAge = intAge.To<double>();
var decimalAge = doubleAge.To<decimal>();
