public static void SpinThread<T>(this T parms, Action<T> action)
{
    System.Threading.Thread t =
           new System.Threading.Thread(new System.Threading.ParameterizedThreadStart(p => action((T)p)));
    t.IsBackground = true;
    t.Start(parms);
}

