public static void SetLabelsToSentences(
    this XamDataGrid xamDataGrid)
{
    foreach (
        Field field in xamDataGrid.FieldLayouts[0].Fields)
    {
        xamDataGrid.SetLabel(
            field.Name, field.Name.ToSentence());
    }
}
