public static void ForEachControl(this ControlCollection controlCollection, Action<Control> action)
{
    if (controlCollection == null)
        return;
    foreach (Control c in controlCollection)
    {
        action(c);
        if (c.HasControls())
        {
            ForEachControl(c.Controls, action);
        }
    }
}

