}public static bool IsBoolean(this string value) {
     var val = value.ToLower().Trim();
     if (val == "false")
          return false;
     if (val == "f")
          return false;
     if (val == "true")
          return true;
     if (val == "t")
          return true;
     if (val == "yes")
          return true;
     if (val == "no")
          return false;
     if (val == "y")
          return true;
     if (val == "n")
          return false;
     throw new ArgumentException("Value is not a boolean value.");
}
