public static bool IsDate(this string input)
{
    if (!string.IsNullOrEmpty(input))
    {
        DateTime dt;
        return (DateTime.TryParse(input, out dt));
    }
    else
    {
        return false;
    }
}

string nonDate = "Foo";
string someDate = "Jan 1 2010";

bool isDate = nonDate.IsDate(); //false
isDate = someDate.IsDate(); //true
