/// <summary>
	/// if the string is NULL, converts it to string.empty. Helpful when trying to avoid null conditions.
	/// </summary>
	/// <param name="inString"></param>
	/// <returns></returns>
	public static string IsNullThenEmpty(this string inString)
	{
		if (inString==null)
			return string.Empty;
		else
			return inString;
	}
