public static string CSVQuoted( this string s )
{
    if ( s.IndexOfAny(" ,\n".ToCharArray()) < 0 && s.Trim() == s )
        return s;

    StringBuilder sb = new StringBuilder();
    sb.Append( '"' );
    foreach ( char c in s )
    {
        sb.Append(c);
        if ( c == '"' )
            sb.Append(c);
    }
    sb.Append( '"' );
    return sb.ToString();
}
