public static class MehrdadExtensions
{
    public static string LastChar(this string input)
    {
        if (!string.IsNullOrEmpty(input))
        {
            if (input.Length >= 1)
            {
                return input.Substring(input.Length - 1, 1);
            }
            else
            {
                return input;
            }
        }
        else
        {
            return null;
        }
    }
}

