public static T FindImmediateParentOfType<T>(this Control control) where T : Control
    {
        T retVal = default(T);
        Control parentCtl = control.Parent;
        while (parentCtl != null)
        {
            if (parentCtl is T)
            {
                retVal = (T)parentCtl;
                break;
            }
            else
            {
                parentCtl = parentCtl.Parent;
            }
        }
        return retVal;
 
    }
