public static string IsNotNullThenTrim(this string s)
{
    if (!string.IsNullOrEmpty(s))
        return s.Trim();
    else
        return s;
}

string n = null;
Assert.IsNull(n.IsNotNullThenTrim());

string s = "test ";
Assert.IsNotNull(s.IsNotNullThenTrim());
Assert.AreEqual("test", s.IsNotNullThenTrim());
