public static Decimal timeToDecimal(this string time)
        {
            int Hours = time.Split(':')[0].ToInt();
            decimal Minutes = time.Split(':')[1].ToInt();
            while (Minutes >= 60)
            {
                Minutes = Minutes % 60;
                Hours ++;
            }
            //Minutes = Minutes/60;
            long test = Convert.ToInt64((Minutes/60)/10);
            return Hours + Minutes/60;
        }
