public static XElement FindParent(this XElement e, string Name)
        {
            XElement r = null;

            if (e == null)
                return r;
            
            if (e.Parent != null && e.Parent.Name == Name)
            {
                r = e.Parent;
            }
            else
            {
                r = e.Parent.FindParent(Name);
            }

            return r;
        }
