using namespace std;

class myclass

{

public:

int a;

int *stack2;

int front;

int rear;

myclass(int x);

void add(int i,int item);

int del(int i);

};

myclass ::myclass(int x)

{

stack2=new int[x];

a=x;

front=0;

rear=a-1;

}

void myclass::add(int i,int item)

{

if(front==rear)

cout<<"\n"<<"@@@@@@@@@@ stack is full @@@@@@@@@"<<"\n";

else

{

if(i==0)

{

stack2[++front]=item;

}

if(i==1)

{

stack2[--rear]=item;

}

}

}

int myclass::del(int i)

{

if(front==0 && rear==a-1)

{

cout<<" @@@@@@ stack is empty @@@@@@";

}

else

{

if(i==0)

{

return stack2[--front];

}

if(i==1)

{

return stack2[++rear];

}

}

}

